import VueRouter from 'vue-router'
import App from '../App.vue'
import SingleProduct from '../SingleProduct.vue'



const routes = [
  {
    path: '/',
    name: 'home',
    component: App
  },
  {
    path: '/product/:id',
    name: 'SingleProduct',
    component: SingleProduct
  }
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

export default router
